import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [editing, setEditing] = useState(false);
  const nav = useNavigate();
  const { userId } = useParams();
  const [updateCount, setUpdateCount] = useState(0);
  const [formData, setFormData] = useState({
    dob_day: user.dob_day,
    dob_month: user.dob_month,
    dob_year: user.dob_year,
    url: user.url,
    about: user.about
});

  useEffect(() => {
    getUser();
  }, [userId, updateCount]);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevState) => ({
        ...prevState,
        [name]: value
    }));
};

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData(event.target);
    const data = {
        formData: formData,
        user_id: userId // Add the user_id obtained from useParams
    };
    console.log(data);
    try {
      const response = await axios.put('http://localhost:8000/user', data);
      setUser(response.data);
      setEditing(false);
      setUpdateCount(updateCount + 1);
    } catch (error) {
      console.log(error);
    }
};



  const getUser = async () => {
    try {
      const response = await axios.get('http://localhost:8000/user', {
        params: { userId },
      });
      setUser(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleEdit = () => {
    setEditing(true);
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className='profileback'>
      <div className='card profile-card'>
        <div className='profile-container'>
          <div className='profile-image'>
            <img src={user.url} alt='' />
          </div>
          <div className='profile-details'>
            <h2>{user.first_name}</h2>
            <p>Email: {user.email}</p>
            {editing ? (
    <form onSubmit={handleSubmit}>
        <input type="hidden" name="user_id" value={user.user_id} />
        <label htmlFor='dob_day'>Day of Birth</label>
        <input type='number' name='dob_day' value={formData.dob_day} onChange={handleInputChange} />
        <label htmlFor='dob_month'>Month of Birth</label>
        <input type='number' name='dob_month' value={formData.dob_month} onChange={handleInputChange} />
        <label htmlFor='dob_year'>Year of Birth</label>
        <input type='number' name='dob_year' value={formData.dob_year} onChange={handleInputChange} />
        <label htmlFor='url'>Profile Image URL</label>
        <input type='url' name='url' value={formData.url} onChange={handleInputChange} />
        <label htmlFor='about'>About</label>
        <textarea name='about' value={formData.about} onChange={handleInputChange}></textarea>
        <button type='submit'>Save</button>
    </form>
            ) : (
              <>
                <p>Bio: {user.about}</p>
                <p>Age: {2023 - user.dob_year}</p>
                <p>Date of Birth: {user.dob_day}-{user.dob_month}-{user.dob_year}</p>
                <button onClick={handleEdit}>Edit Profile</button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
